mapper.select:
SELECT mbsp_id, mbsp_name, mbsp_email, mbsp_password, mbsp_zipcode, mbsp_addr, mbsp_deaddr, mbsp_phone, mbsp_nick, mbsp_receive, mbsp_point, mbsp_lastlogin, mbsp_datesub, mbsp_updatedate FROM ( SELECT /*+ INDEX_DESC(mbsp_tbl PK_MBSP_ID) */ ROWNUM AS RN, mbsp_id, mbsp_name, mbsp_email, mbsp_password, mbsp_zipcode, mbsp_addr, mbsp_deaddr, mbsp_phone, mbsp_nick, mbsp_receive, mbsp_point, mbsp_lastlogin, mbsp_datesub, mbsp_updatedate FROM mbsp_tbl WHERE
ROWNUM <= (#{pageNum} * #{amount})
) WHERE RN > (#{pageNum} - 1) * #{amount}

SELECT mail_idx, mail_title, mail_content, mail_kind, mail_send_count, reg_date FROM ( SELECT /*+ INDEX_DESC(mailing_tbl PK_MAILING_IDX) */ ROWNUM AS RN, mail_idx, mail_title, mail_content, mail_kind, mail_send_count, reg_date FROM mailing_tbl WHERE 1 = 1
AND ROWNUM <= (#{pageNum} * #{amount})
) WHERE RN > ((#{pageNum} -1) * #{amount})
