mapper.select:
SELECT ord_code, mbsp_id, ord_name, ord_tel, ord_regdate, ord_addr_zipcode, ord_addr_basic, ord_addr_detail, ord_price, ord_desc, ord_admin_memo FROM ( SELECT /*+ INDEX_DESC (order_tbl PK_ORD_CODE)*/ ROWNUM AS RN, ord_code, mbsp_id, ord_name, ord_tel, ord_regdate, ord_addr_zipcode, ord_addr_basic, ord_addr_detail, ord_price, ord_desc, ord_admin_memo FROM order_tbl WHERE
ROWNUM <= (#{cri.pageNum} * #{cri.amount})
) WHERE RN > (#{cri.pageNum} - 1) * #{cri.amount}
