mapper.select:
SELECT re_code, mbsp_id, pro_num, pro_name, pro_up_folder, pro_img, re_title, re_content, re_rate, re_date, re_admin_reply_content, re_admin_reply_date, re_admin_reply_status FROM ( SELECT /*+ INDEX_DESC (r PK_RE_CODE) USE_NL(r p) */ ROWNUM AS RN, r.re_code, r.mbsp_id, r.pro_num, p.pro_name, p.pro_up_folder, p.pro_img, r.re_title, r.re_content, r.re_rate, r.re_date, r.re_admin_reply_content, r.re_admin_reply_date, r.re_admin_reply_status FROM review_tbl r JOIN product_tbl p ON p.pro_num = r.pro_num WHERE
ROWNUM <= (#{pageNum} * #{amount})
) WHERE RN > (#{pageNum} - 1) * #{amount}
