mapper.select:
SELECT
	mbsp_email
FROM
	mbsp_tbl
WHERE
	mbsp_id = #{mbsp_id}
AND
	mbsp_name = #{mbsp_name}				
AND
	mbsp_email = #{mbsp_email}

mapper.update:
UPDATE
	mbsp_tbl
SET
	mbsp_password = #{temp_enc_pw}
WHERE
	mbsp_id = #{mbsp_id}

UPDATE
	mbsp_tbl
SET
	mbsp_password = #{new_mbsp_password}
WHERE
	mbsp_id = #{mbsp_id}
